import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export function EditJob() {
  const { jobId } = useParams();
  const navigate = useNavigate();

  const [jobData, setJobData] = useState({
    title: "",
    company: "",
    location: "",
    type: "",
    phone: ""
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  // -------- Fetch job info --------
  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await fetch(`http://localhost:3001/jobs/${jobId}`);
        const data = await res.json();
        if (res.ok) setJobData(data.job);
        else alert("Job not found!");
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchJob();
  }, [jobId]);

  // -------- Handle input change --------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setJobData(prev => ({ ...prev, [name]: value }));
  };

  // -------- Save updates --------
  const handleSave = async (e) => {
    e.preventDefault();
    setSaving(true);
    try {
      const res = await fetch(`http://localhost:3001/jobs/${jobId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(jobData)
      });
      if (res.ok) {
        alert("Job updated successfully!");
        navigate("/settings");
      } else {
        alert("Failed to update job.");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating job.");
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <p>Loading job info...</p>;

  return (
    <div className="edit-job-page">
      <h2>Edit Job</h2>
      <form onSubmit={handleSave}>
        <label>
          Job Title:
          <input
            type="text"
            name="title"
            value={jobData.title}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Company:
          <input
            type="text"
            name="company"
            value={jobData.company}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Location:
          <input
            type="text"
            name="location"
            value={jobData.location}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Type:
          <select name="type" value={jobData.type} onChange={handleChange} required>
            <option value="">Select Type</option>
            <option value="Full-time">Full-time</option>
            <option value="Part-time">Part-time</option>
            <option value="Internship">Internship</option>
            <option value="Contract">Contract</option>
          </select>
        </label>

        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={jobData.phone}
            onChange={handleChange}
            required
          />
        </label>

        <button type="submit" disabled={saving}>
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </form>
      <button onClick={() => navigate("/settings")}>Cancel</button>
    </div>
  );
}
