import React, { useEffect, useState } from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";
import { ArrowLeft, Trash2, Edit } from "lucide-react";
import avatarImg from "../Images/avatar.png";
import "./Settings.css";

export function Settings() {
  const { user } = useAppContext();
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [loadingEvents, setLoadingEvents] = useState(true);
  const [loadingJobs, setLoadingJobs] = useState(true);

  // للحفاظ على بيانات التعديل
  const [editingEvent, setEditingEvent] = useState(null);
  const [editingJob, setEditingJob] = useState(null);

  // جلب الأحداث
  useEffect(() => {
    if (!user?.id) {
      setLoadingEvents(false);
      return;
    }
    const fetchEvents = async () => {
      try {
        const res = await fetch(`http://localhost:3001/events/user/${user.id}`);
        const data = await res.json();
        setEvents(data.events || []);
      } catch {
        setEvents([]);
      } finally {
        setLoadingEvents(false);
      }
    };
    fetchEvents();
  }, [user]);

  // جلب الوظائف
  useEffect(() => {
    if (!user?.id) {
      setLoadingJobs(false);
      return;
    }
    const fetchJobs = async () => {
      try {
        const res = await fetch(`http://localhost:3001/jobs/user/${user.id}`);
        const data = await res.json();
        setJobs(data.jobs || []);
      } catch {
        setJobs([]);
      } finally {
        setLoadingJobs(false);
      }
    };
    fetchJobs();
  }, [user]);

  const handleBack = () => navigate("/home");

  const handleDeleteEvent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    try {
      const res = await fetch(`http://localhost:3001/events/${id}`, { method: "DELETE" });
      if (res.ok) setEvents(prev => prev.filter(e => e._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteJob = async (id) => {
    if (!window.confirm("Are you sure you want to delete this job?")) return;
    try {
      const res = await fetch(`http://localhost:3001/jobs/${id}`, { method: "DELETE" });
      if (res.ok) setJobs(prev => prev.filter(j => j._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  // تعديل حدث
  const handleUpdateEvent = (event) => setEditingEvent(event);

  const saveEventUpdate = async () => {
    try {
      const res = await fetch(`http://localhost:3001/events/${editingEvent._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editingEvent),
      });
      const data = await res.json();
      setEvents(prev => prev.map(e => e._id === data.event._id ? data.event : e));
      setEditingEvent(null);
    } catch (err) {
      console.error(err);
    }
  };

  // تعديل وظيفة
  const handleUpdateJob = (job) => setEditingJob(job);

  const saveJobUpdate = async () => {
    try {
      const res = await fetch(`http://localhost:3001/jobs/${editingJob._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editingJob),
      });
      const data = await res.json();
      setJobs(prev => prev.map(j => j._id === data.job._id ? data.job : j));
      setEditingJob(null);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="settings-page">
      <div className="settings-header">
        <button className="back-btn" onClick={handleBack}>
          <ArrowLeft size={18} /> Back
        </button>
        <h1>Settings</h1>
      </div>

      {/* User Info */}
      <div className="user-info">
        <img src={avatarImg} alt="avatar" className="user-avatar" />
        <div className="user-details">
          {user ? (
            <>
              <h2>{user.name}</h2>
              <p>{user.email}</p>
            </>
          ) : (
            <p>Loading user info...</p>
          )}
        </div>
      </div>

      {/* Events */}
      <div className="user-events">
        <h3>My Events</h3>
        {loadingEvents ? <p>Loading...</p> :
          events.length === 0 ? <p>No events registered.</p> :
          <ul>
            {events.map(ev => (
              <li key={ev._id} className="event-card">
                {editingEvent && editingEvent._id === ev._id ? (
                  <div>
                    <input
                      value={editingEvent.age}
                      onChange={e => setEditingEvent({...editingEvent, age: e.target.value})}
                    />
                    <select
                      value={editingEvent.gender}
                      onChange={e => setEditingEvent({...editingEvent, gender: e.target.value})}
                    >
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                    </select>
                    <select
                      value={editingEvent.experience}
                      onChange={e => setEditingEvent({...editingEvent, experience: e.target.value})}
                    >
                      <option value="Beginner">Beginner</option>
                      <option value="Intermediate">Intermediate</option>
                      <option value="Advanced">Advanced</option>
                    </select>
                    <button onClick={saveEventUpdate}>Save</button>
                    <button onClick={() => setEditingEvent(null)}>Cancel</button>
                  </div>
                ) : (
                  <>
                    <h4>{ev.eventTitle}</h4>
                    <p><strong>Phone:</strong> {ev.phone}</p>
                    <p><strong>Age:</strong> {ev.age}</p>
                    <p><strong>Gender:</strong> {ev.gender}</p>
                    <p><strong>Experience:</strong> {ev.experience}</p>
                    <div className="event-actions">
                      <button onClick={() => handleUpdateEvent(ev)}>Update</button>
                      <button onClick={() => handleDeleteEvent(ev._id)}>Delete</button>
                    </div>
                  </>
                )}
              </li>
            ))}
          </ul>
        }
      </div>

      {/* Jobs */}
      <div className="user-jobs">
        <h3>My Job Registrations</h3>
        {loadingJobs ? <p>Loading...</p> :
          jobs.length === 0 ? <p>No jobs registered.</p> :
          <ul>
            {jobs.map(job => (
              <li key={job._id} className="job-card">
                {editingJob && editingJob._id === job._id ? (
                  <div>
                    <input
                      value={editingJob.type}
                      onChange={e => setEditingJob({...editingJob, type: e.target.value})}
                    />
                    <input
                      value={editingJob.phone}
                      onChange={e => setEditingJob({...editingJob, phone: e.target.value})}
                    />
                    <button onClick={saveJobUpdate}>Save</button>
                    <button onClick={() => setEditingJob(null)}>Cancel</button>
                  </div>
                ) : (
                  <>
                    <h4>{job.title}</h4>
                    <p><strong>Company:</strong> {job.company}</p>
                    <p><strong>Location:</strong> {job.location}</p>
                    <p><strong>Type:</strong> {job.type}</p>
                    <p><strong>Phone:</strong> {job.phone}</p>
                    <div className="job-actions">
                      <button onClick={() => handleUpdateJob(job)}>Update</button>
                      <button onClick={() => handleDeleteJob(job._id)}>Delete</button>
                    </div>
                  </>
                )}
              </li>
            ))}
          </ul>
        }
      </div>
    </div>
  );
}
