import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export function EditJob() {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState({
    title: "",
    company: "",
    location: "",
    type: "",
    phone: "",
  });
  const [loading, setLoading] = useState(true);

  // Fetch job data
  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await fetch(`http://localhost:3001/jobs/user/${jobId}`);
        const data = await res.json();
        const jb = data.jobs.find(j => j._id === jobId);
        if (jb) setJob(jb);
      } catch (err) {
        console.error("Failed to fetch job", err);
      } finally {
        setLoading(false);
      }
    };
    fetchJob();
  }, [jobId]);

  const handleChange = e => {
    const { name, value } = e.target;
    setJob(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:3001/jobs/${jobId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(job),
      });
      if (res.ok) {
        alert("Job updated successfully!");
        navigate("/settings");
      }
    } catch (err) {
      console.error(err);
      alert("Failed to update job");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="update-page">
      <h2>Edit Job</h2>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input name="title" value={job.title} onChange={handleChange} required />

        <label>Company:</label>
        <input name="company" value={job.company} onChange={handleChange} required />

        <label>Location:</label>
        <input name="location" value={job.location} onChange={handleChange} required />

        <label>Type:</label>
        <input name="type" value={job.type} onChange={handleChange} required />

        <label>Phone:</label>
        <input name="phone" value={job.phone} onChange={handleChange} required />

        <button type="submit">Update Job</button>
      </form>
    </div>
  );
}
