// src/components/EditJob.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./Settings.css";

export function EditJob() {
  const { jobId } = useParams();
  const navigate = useNavigate();

  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);

  // جلب بيانات الوظيفة
  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await fetch(`http://localhost:3001/jobs/user/${jobId}`);
        const data = await res.json();
        const foundJob = data.jobs.find(j => j._id === jobId);
        setJob(foundJob);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchJob();
  }, [jobId]);

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const res = await fetch(`http://localhost:3001/jobs/${jobId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(job),
      });
      if (res.ok) {
        alert("Job updated successfully!");
        navigate("/settings");
      }
    } catch (err) {
      console.error(err);
      alert("Failed to update job.");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!job) return <p>Job not found</p>;

  return (
    <div className="settings-page">
      <h1>Edit Job</h1>
      <div className="user-jobs">
        <label>Title:</label>
        <input name="title" value={job.title} onChange={handleChange} />

        <label>Company:</label>
        <input name="company" value={job.company} onChange={handleChange} />

        <label>Location:</label>
        <input name="location" value={job.location} onChange={handleChange} />

        <label>Type:</label>
        <input name="type" value={job.type} onChange={handleChange} />

        <label>Phone:</label>
        <input name="phone" value={job.phone} onChange={handleChange} />

        <button className="edit-btn" onClick={handleSave}>Save Changes</button>
      </div>
    </div>
  );
}
