// src/components/EditJob.js
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

export function EditJob() {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [jobData, setJobData] = useState({
    title: "",
    company: "",
    location: "",
    type: "",
    phone: "",
    salary: "",
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await fetch(`http://localhost:3001/jobs/user/${jobId}`);
        const data = await res.json();
        const job = data.jobs.find(j => j._id === jobId);
        if (job) setJobData({
          title: job.title,
          company: job.company,
          location: job.location,
          type: job.type,
          phone: job.phone,
          salary: job.salary,
        });
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchJob();
  }, [jobId]);

  const handleChange = (e) => {
    setJobData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleUpdate = async () => {
    try {
      const res = await fetch(`http://localhost:3001/jobs/${jobId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(jobData),
      });
      if (res.ok) navigate("/settings");
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="edit-page">
      <h2>Edit Job</h2>
      <input name="title" value={jobData.title} onChange={handleChange} placeholder="Job Title" />
      <input name="company" value={jobData.company} onChange={handleChange} placeholder="Company" />
      <input name="location" value={jobData.location} onChange={handleChange} placeholder="Location" />
      <input name="type" value={jobData.type} onChange={handleChange} placeholder="Type" />
      <input name="salary" value={jobData.salary} onChange={handleChange} placeholder="Salary" />
      <input name="phone" value={jobData.phone} onChange={handleChange} placeholder="Phone" />
      <button onClick={handleUpdate}>Update</button>
      <button onClick={() => navigate("/settings")}>Cancel</button>
    </div>
  );
}
