import React, { useState, useEffect } from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";

const jobOptions = [
  { title: "Graphic Designer", company: "Company XYZ", location: "Location XYZ", type: "Full-time" },
  { title: "UI/UX Designer", company: "TechCorp", location: "Remote", type: "Full-time" },
  // أضف باقي الوظائف هنا
];

export function JobRegister() {
  const { user } = useAppContext();
  const navigate = useNavigate();

  const [jobTitle, setJobTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [type, setType] = useState("");
  const [phone, setPhone] = useState("");

  useEffect(() => {
    const job = jobOptions.find(j => j.title === jobTitle);
    if (job) {
      setCompany(job.company);
      setLocation(job.location);
      setType(job.type);
    } else {
      setCompany("");
      setLocation("");
      setType("");
    }
  }, [jobTitle]);

  const handleSubmit = async () => {
    if (!jobTitle || !phone) return alert("Please fill all fields");
    if (!user?.id) return alert("User not logged in");

    try {
      const res = await fetch("http://localhost:3001/registerJob", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user.id,
          jobId: jobTitle,
          title: jobTitle,
          company,
          location,
          type,
          phone,
        }),
      });

      const data = await res.json();

      if (res.ok || data.job) {
        alert("Job registration successful!");
        setJobTitle("");
        setPhone("");
        navigate("/settings");
      } else {
        alert(data.error || "Registration failed");
      }
    } catch (err) {
      console.error("Job registration error:", err);
      alert("Server error. Please try again.");
    }
  };

  return (
    <div className="job-register-container">
      <h1>Register Job</h1>

      <select value={jobTitle} onChange={e => setJobTitle(e.target.value)}>
        <option value="">Select Job</option>
        {jobOptions.map(j => (
          <option key={j.title} value={j.title}>{j.title}</option>
        ))}
      </select>

      <input type="text" value={company} placeholder="Company" disabled />
      <input type="text" value={location} placeholder="Location" disabled />
      <input type="text" value={type} placeholder="Job Type" disabled />

      <input
        type="text"
        placeholder="Phone"
        value={phone}
        onChange={e => setPhone(e.target.value)}
      />

      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}
