import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export function EditJob() {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/jobs/${jobId}`)
      .then(res => res.json())
      .then(data => setJob(data.job))
      .catch(err => console.error(err));
  }, [jobId]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    await fetch(`http://localhost:3001/jobs/${jobId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(job),
    });
    navigate("/settings");
  };

  if (!job) return <p>Loading...</p>;

  return (
    <form onSubmit={handleUpdate} style={{ maxWidth: "400px", margin: "20px auto" }}>
      <h2>Edit Job</h2>
      <input value={job.title} onChange={e => setJob({ ...job, title: e.target.value })} placeholder="Job Title" />
      <input value={job.company} onChange={e => setJob({ ...job, company: e.target.value })} placeholder="Company" />
      <input value={job.location} onChange={e => setJob({ ...job, location: e.target.value })} placeholder="Location" />
      <input value={job.type} onChange={e => setJob({ ...job, type: e.target.value })} placeholder="Type" />
      <input value={job.phone} onChange={e => setJob({ ...job, phone: e.target.value })} placeholder="Phone" />
      <button type="submit">Update Job</button>
    </form>
  );
}
