// src/components/EditEvent.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./Settings.css";

export function EditEvent() {
  const { eventId } = useParams();
  const navigate = useNavigate();

  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);

  // جلب بيانات الحدث
  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const res = await fetch(`http://localhost:3001/events/user/${eventId}`);
        const data = await res.json();
        const foundEvent = data.events.find(ev => ev._id === eventId);
        setEvent(foundEvent);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchEvent();
  }, [eventId]);

  const handleChange = (e) => {
    setEvent({ ...event, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const res = await fetch(`http://localhost:3001/events/${eventId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(event),
      });
      if (res.ok) {
        alert("Event updated successfully!");
        navigate("/settings");
      }
    } catch (err) {
      console.error(err);
      alert("Failed to update event.");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!event) return <p>Event not found</p>;

  return (
    <div className="settings-page">
      <h1>Edit Event</h1>
      <div className="user-events">
        <label>Event Title:</label>
        <input name="eventTitle" value={event.eventTitle} onChange={handleChange} />

        <label>Phone:</label>
        <input name="phone" value={event.phone} onChange={handleChange} />

        <label>Age:</label>
        <input name="age" type="number" value={event.age} onChange={handleChange} />

        <label>Gender:</label>
        <select name="gender" value={event.gender} onChange={handleChange}>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>

        <label>Experience:</label>
        <select name="experience" value={event.experience} onChange={handleChange}>
          <option value="Beginner">Beginner</option>
          <option value="Intermediate">Intermediate</option>
          <option value="Advanced">Advanced</option>
        </select>

        <button className="edit-btn" onClick={handleSave}>Save Changes</button>
      </div>
    </div>
  );
}
