import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

export function EditJob() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "",
    company: "",
    location: "",
    type: "",
    phone: ""
  });

  useEffect(() => {
    fetch(`http://localhost:3001/jobs/user/${id}`)
      .then(res => res.json())
      .then(data => {
        const job = data.jobs.find(j => j._id === id);
        if (job) setForm({
          title: job.title,
          company: job.company,
          location: job.location,
          type: job.type,
          phone: job.phone
        });
      });
  }, [id]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch(`http://localhost:3001/jobs/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });
    navigate("/settings");
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Edit Job</h2>
      <form onSubmit={handleSubmit}>
        <input name="title" value={form.title} onChange={handleChange} placeholder="Job Title" />
        <input name="company" value={form.company} onChange={handleChange} placeholder="Company" />
        <input name="location" value={form.location} onChange={handleChange} placeholder="Location" />
        <input name="type" value={form.type} onChange={handleChange} placeholder="Type" />
        <input name="phone" value={form.phone} onChange={handleChange} placeholder="Phone" />
        <button type="submit">Save</button>
      </form>
    </div>
  );
}
