// JobsRegister.js
import React, { useState } from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";
import "./JobsRegister.css";

export function JobRegister() { // الاسم مطابق للصادر في CSS
  const { user } = useAppContext();
  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [type, setType] = useState("");
  const [phone, setPhone] = useState("");

  const handleSubmit = async () => {
    if (!title || !company || !location || !type || !phone) {
      return alert("Please fill all fields");
    }
    if (!user?.id) return alert("User not logged in");

    try {
      const res = await fetch("http://localhost:3001/registerJob", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user.id,
          jobId: Date.now().toString(), // معرف مؤقت
          title,
          company,
          location,
          type,
          phone,
        }),
      });

      const data = await res.json();
      if (res.ok) {
        alert("Job registration successful!");
        setTitle("");
        setCompany("");
        setLocation("");
        setType("");
        setPhone("");
        navigate("/settings");
      } else {
        alert(data.error || "Registration failed");
      }
    } catch (err) {
      console.error("Job registration error:", err);
      alert("Server error. Please try again.");
    }
  };

  return (
    <div className="jobs-register-page">
      <div className="jobs-register-container">
        <h1>Register for Job</h1>

        <input
          type="text"
          placeholder="Job Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <input
          type="text"
          placeholder="Company"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
        />

        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />

        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="">Select Job Type</option>
          <option value="Full-time">Full-time</option>
          <option value="Part-time">Part-time</option>
          <option value="Internship">Internship</option>
        </select>

        <input
          type="text"
          placeholder="Phone Number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />

        <button className="btn-register" onClick={handleSubmit}>
          Submit
        </button>
      </div>
    </div>
  );
}
