import React, { useState } from "react";
import "./JobsRegister.css";

const companies = {
  "TechCorp": { location: "Remote" },
  "Company XYZ": { location: "Location XYZ" },
  "DesignHub": { location: "New York" },
  "Web Solutions": { location: "London" },
};

export function JobsRegister({ userId }) {
  const [jobTitle, setJobTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [type, setType] = useState("");
  const [phone, setPhone] = useState("");

  const handleCompanyChange = (e) => {
    const selected = e.target.value;
    setCompany(selected);
    setLocation(companies[selected]?.location || "");
  };

  const handleSubmit = async () => {
    if (!jobTitle || !company || !type || !phone) {
      return alert("Please fill all required fields");
    }

    const payload = {
      userId,
      jobId: Date.now().toString(), // مجرد معرف مؤقت
      title: jobTitle,
      company,
      location,
      type,
      phone,
    };

    try {
      const res = await fetch("http://localhost:3001/registerJob", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      if (res.ok) {
        alert("Job registered successfully!");
        setJobTitle("");
        setCompany("");
        setLocation("");
        setType("");
        setPhone("");
      } else {
        alert(data.error || "Registration failed");
      }
    } catch (err) {
      console.error(err);
      alert("Server error, try again");
    }
  };

  return (
    <div className="job-register-container">
      <h2>Register for Job</h2>

      <input
        type="text"
        placeholder="Job Title"
        value={jobTitle}
        onChange={(e) => setJobTitle(e.target.value)}
      />

      <select value={company} onChange={handleCompanyChange}>
        <option value="">Select Company</option>
        {Object.keys(companies).map((c) => (
          <option key={c} value={c}>{c}</option>
        ))}
      </select>

      <input
        type="text"
        placeholder="Location"
        value={location}
        disabled
      />

      <select value={type} onChange={(e) => setType(e.target.value)}>
        <option value="">Select Job Type</option>
        <option value="Full-time">Full-time</option>
        <option value="Part-time">Part-time</option>
        <option value="Internship">Internship</option>
      </select>

      <input
        type="text"
        placeholder="Phone Number"
        value={phone}
        onChange={(e) => setPhone(e.target.value)}
      />

      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}
