// src/components/Settings.js
import React, { useEffect, useState } from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";
import { ArrowLeft, Trash2, Edit3 } from "lucide-react";
import avatarImg from "../Images/avatar.png";
import "./Settings.css";

export function Settings() {
  const { user } = useAppContext();
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [loadingEvents, setLoadingEvents] = useState(true);
  const [loadingJobs, setLoadingJobs] = useState(true);

  // جلب الأحداث
  useEffect(() => {
    if (!user?.id) {
      setLoadingEvents(false);
      return;
    }
    const fetchEvents = async () => {
      try {
        const res = await fetch(`http://localhost:3001/events/user/${user.id}`);
        const data = await res.json();
        setEvents(data.events || []);
      } catch (err) {
        console.error("Failed to load events", err);
        setEvents([]);
      } finally {
        setLoadingEvents(false);
      }
    };
    fetchEvents();
  }, [user]);

  // جلب الوظائف
  useEffect(() => {
    if (!user?.id) {
      setLoadingJobs(false);
      return;
    }
    const fetchJobs = async () => {
      try {
        const res = await fetch(`http://localhost:3001/jobs/user/${user.id}`);
        const data = await res.json();
        setJobs(data.jobs || []);
      } catch (err) {
        console.error("Failed to load jobs", err);
        setJobs([]);
      } finally {
        setLoadingJobs(false);
      }
    };
    fetchJobs();
  }, [user]);

  const handleBack = () => navigate("/home");

  const handleDeleteEvent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    try {
      const res = await fetch(`http://localhost:3001/events/${id}`, { method: "DELETE" });
      if (res.ok) setEvents(prev => prev.filter(e => e._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteJob = async (id) => {
    if (!window.confirm("Are you sure you want to delete this job registration?")) return;
    try {
      const res = await fetch(`http://localhost:3001/jobs/${id}`, { method: "DELETE" });
      if (res.ok) setJobs(prev => prev.filter(j => j._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="settings-page">
      <div className="settings-header">
        <button className="back-btn" onClick={handleBack}>
          <ArrowLeft size={18} /> Back
        </button>
        <h1>Settings</h1>
      </div>

      {/* معلومات المستخدم */}
      <div className="user-info">
        <img src={avatarImg} alt="avatar" className="user-avatar" />
        <div className="user-details">
          {user ? (
            <>
              <h2>{user.name}</h2>
              <p>{user.email}</p>
            </>
          ) : (
            <p>Loading user info...</p>
          )}
        </div>
      </div>

      {/* الأحداث */}
      <div className="user-events">
        <h3>My Events</h3>
        {loadingEvents ? (
          <p>Loading...</p>
        ) : events.length === 0 ? (
          <p>No events registered.</p>
        ) : (
          <ul>
            {events.map(ev => (
              <li key={ev._id} className="event-card">
                <h4>{ev.eventTitle}</h4>
                <p><strong>Phone:</strong> {ev.phone}</p>
                <p><strong>Age:</strong> {ev.age}</p>
                <p><strong>Gender:</strong> {ev.gender}</p>
                <p><strong>Experience:</strong> {ev.experience}</p>
                <div className="event-actions">
                  <button className="edit-btn" onClick={() => navigate(`/update-event/${ev._id}`)}>
                    <Edit3 size={14} /> Edit
                  </button>
                  <button className="delete-btn" onClick={() => handleDeleteEvent(ev._id)}>
                    <Trash2 size={14} /> Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* الوظائف */}
      <div className="user-jobs">
        <h3>My Job Registrations</h3>
        {loadingJobs ? (
          <p>Loading...</p>
        ) : jobs.length === 0 ? (
          <p>No jobs registered.</p>
        ) : (
          <ul>
            {jobs.map(job => (
              <li key={job._id} className="job-card">
                <h4>{job.title}</h4>
                <p><strong>Company:</strong> {job.company}</p>
                <p><strong>Location:</strong> {job.location}</p>
                <p><strong>Type:</strong> {job.type}</p>
                <p><strong>Phone:</strong> {job.phone}</p>
                <div className="job-actions">
                  <button className="edit-btn" onClick={() => navigate(`/update-job/${job._id}`)}>
                    <Edit3 size={14} /> Edit
                  </button>
                  <button className="delete-btn" onClick={() => handleDeleteJob(job._id)}>
                    <Trash2 size={14} /> Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
