import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../Store/store";
import "./JobsRegister.css";

export function JobsRegister() {
  const { user } = useAppContext();
  const navigate = useNavigate();

  const [civilId, setCivilId] = useState("");
  const [jobId, setJobId] = useState("");

  // قائمة الوظائف الجاهزة
  const jobOptions = [
    { id: "job1", title: "Software Developer" },
    { id: "job2", title: "Data Analyst" },
    { id: "job3", title: "Graphic Designer" },
    { id: "job4", title: "Project Manager" },
  ];

  const handleSubmit = async () => {
    if (!civilId || !jobId) return alert("Please fill all fields");

    try {
      const selectedJob = jobOptions.find(job => job.id === jobId);

      const res = await fetch("http://localhost:3001/registerJob", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user?.id,
          jobId: selectedJob.id,
          title: selectedJob.title,
          company: "Company XYZ", // يمكنك تخصيصها أو جعلها خيار
          location: "Location XYZ", // يمكن تخصيصها أيضاً
          type: "Full-time", // يمكن تعديل حسب الحاجة
          phone: civilId, // رقمك المدني
        }),
      });

      const data = await res.json();

      if (res.ok) {
        alert("Job registration successful!");
        setCivilId("");
        setJobId("");
        navigate("/settings"); // بعد التسجيل
      } else {
        alert(data.error || "Registration failed");
      }
    } catch (err) {
      console.error("Job registration error:", err);
      alert("Server error. Please try again.");
    }
  };

  return (
    <div className="jobs-register-page">
      <div className="jobs-register-container">
        <h1>Register Job</h1>

        <input
          type="text"
          placeholder="Civil ID"
          value={civilId}
          onChange={(e) => setCivilId(e.target.value)}
        />

        <select value={jobId} onChange={(e) => setJobId(e.target.value)}>
          <option value="">Select Job</option>
          {jobOptions.map(job => (
            <option key={job.id} value={job.id}>{job.title}</option>
          ))}
        </select>

        <button className="btn-register" onClick={handleSubmit}>
          Submit
        </button>
      </div>
    </div>
  );
}
